;@ EaCalc : Get '(a7)+' into r11:
  and r2,r8,#0x000f
  ldr r11,[r7,r2,lsl #2]
  add r3,r11,#2 ;@ Post-increment An
  str r3,[r7,r2,lsl #2]
;@ EaRead : Read '(a7)+' (address in r11) into r0:
  mov r0,r11
  str r4,[r7,#0x40] ;@ Save PC
  str r5,[r7,#0x5c] ;@ Save Cycles
  mov lr,pc
  ldr pc,[r7,#0x68] ;@ Call read8(r0) handler
